mapper.select:
select t1.*, t2.ds_name, t2.ds_type,t3.version, t3.inner_version from t_data_tables t1, t_dbus_datasource t2, t_meta_version t3 where t1.ds_id = t2.id and t1.ver_id = t3.id
 and t1.ds_id = #{dsID}
 and t1.schema_name = #{schemaName}
 and t1.table_name like  "%"#{tableName}"%"

mapper.update:
update t_data_tables
set status = #{status}
where id = #{id}

update t_data_tables
set ver_change_history = null, ver_change_notice_flg = 0, create_time = create_time
where id = #{id}
